package top.irvingsoft.leetcode.code76;

import java.util.Arrays;
import java.util.HashMap;
import java.util.Map;

/**
 * 最小覆盖子串
 *
 * @author TimeChaser
 * @since 2021/11/6 9:24
 */
public class Solution {

    private static final Map<Character, Integer> cnt = new HashMap<>();
    private static final Map<Character, Integer> ori = new HashMap<>();

    public static void main(String[] args) {
        System.out.println(minWindowHash("ADOBECODEBANC", "ABC"));
        //        System.out.println(minWindow("a", "a"));
        //        System.out.println(minWindow("a", "aa"));
        //        System.out.println(minWindow("bbaa", "aba"));
        //        System.out.println(minWindowHash("",
        //                ""));
    }

    /**
     * 暴力解法
     * <p>
     * 超时
     */
    public static String minWindow(String s, String t) {
        int m = s.length();
        int n = t.length();
        if (m < n) {
            return "";
        }
        int length = n;
        while (length <= m) {
            for (int i = 0; i <= m - length; i++) {
                String substring = s.substring(i, i + length);
                if (isCover(substring, t)) {
                    return substring;
                }
            }
            length++;
        }
        return "";
    }

    public static String minWindowAnother(String s, String t) {
        char[] chars = s.toCharArray(), chart = t.toCharArray();
        int n = chars.length, m = chart.length;

        int[] hash = new int[128];
        for (char ch : chart) {
            hash[ch]--;
        }

        String res = "";
        for (int i = 0, j = 0, cnt = 0; i < n; i++) {
            hash[chars[i]]++;
            if (hash[chars[i]] <= 0) {
                cnt++;
            }
            while (cnt == m && hash[chars[j]] > 0) {
                hash[chars[j++]]--;
            }
            if (cnt == m) {
                if (res.equals("") || res.length() > i - j + 1) {
                    res = s.substring(j, i + 1);
                }
            }
        }
        return res;
    }

    /**
     * 滑动窗口
     */
    public static String minWindowHash(String s, String t) {
        int sLen = s.length();
        int tLen = t.length();
        if (sLen < tLen) {
            return "";
        }
        for (int i = 0; i < tLen; i++) {
            char c = t.charAt(i);
            ori.put(c, ori.getOrDefault(c, 0) + 1);
        }
        int left = 0, right = -1;
        int length = Integer.MAX_VALUE, subIndex = -1;
        while (right++ < sLen) {
            // 右边界右移
            if (right < sLen && ori.containsKey(s.charAt(right))) {
                cnt.put(s.charAt(right), cnt.getOrDefault(s.charAt(right), 0) + 1);
            }
            // 左边界右移
            while (check() && left <= right) {
                if (right - left + 1 < length) {
                    length = right - left + 1;
                    subIndex = left;
                }
                if (ori.containsKey(s.charAt(left))) {
                    cnt.put(s.charAt(left), cnt.getOrDefault(s.charAt(left), 0) - 1);
                }
                left++;
            }
        }
        return subIndex == -1 ? "" : s.substring(subIndex, subIndex + length);
    }

    private static boolean check() {
        for (Map.Entry<Character, Integer> entry : ori.entrySet()) {
            Character key = entry.getKey();
            Integer value = entry.getValue();
            if (cnt.getOrDefault(key, 0) < value) {
                return false;
            }
        }
        return true;
    }

    private static boolean isCover(String s, String p) {
        char[] sChars = s.toCharArray();
        char[] pChars = p.toCharArray();
        Arrays.sort(sChars);
        Arrays.sort(pChars);
        int indexS = 0;
        int indexP = 0;
        while (indexS < sChars.length && indexP < pChars.length) {
            if (sChars[indexS] != pChars[indexP]) {
                indexS++;
            } else {
                indexS++;
                indexP++;
            }
        }
        return indexP == pChars.length;
    }

}
