    /**
     * 栈求解
     * <p>
     * 标记法
     *
     * @author TimeChaser
     * @date 2021/10/21 15:17
     */
    public static int longestValidParenthesesStackMark(String s) {
        int length = s.length();
        int[] mark = new int[length];
        Stack<Integer> stack = new Stack<>();
        for (int i = 0; i < length; i++) {
            if (s.charAt(i) == '(') {
                stack.push(i);
            } else {
                if (stack.isEmpty()) {
                    // 处理不匹配的右括号的标记
                    mark[i] = 1;
                } else {
                    stack.pop();
                }
            }
        }
        // 处理剩余的左括号的标记
        while (!stack.isEmpty()) {
            mark[stack.pop()] = 1;
        }
        int max = 0;
        int count = 0;
        for (int j : mark) {
            // 最长连续标记统计
            if (j == 1) {
                count = 0;
                continue;
            }
            count++;
            max = Math.max(max, count);
        }
        return max;
    }

    /**
     * 栈求解
     * <p>
     * 列举法
     *
     * @author TimeChaser
     * @date 2021/10/21 15:20
     */
    public static int longestValidParenthesesStackLength(String s) {

        int length = s.length();
        int i = length % 2 == 0 ? length : length - 1;
        for (; i > 0; i = i - 2) {
            for (int j = 0; j + i <= length; j++) {
                if (isValid(s.substring(j, i + j))) {
                    return i;
                }
            }
        }
        return 0;
    }

    public static boolean isValid(String s) {

        Stack<Integer> stack = new Stack<>();
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '(') {
                stack.push(i);
            } else {
                if (stack.isEmpty()) {
                    return false;
                }
                stack.pop();
            }
        }
        return stack.isEmpty();
    }

    /**
     * 动态规划
     *
     * @author TimeChaser
     * @date 2021/10/21 15:17
     */
    public static int longestValidParenthesesDynamic(String s) {
        int max = 0;
        int length = s.length();
        int[] dp = new int[length];
        for (int i = 1; i < length; i++) {
            if (s.charAt(i) == ')') {
                if (s.charAt(i - 1) == '(') {
                    // 中间不存在括号对，看左侧是否存在括号对
                    dp[i] = (i > 2 ? dp[i - 2] : 0) + 2;
                } else if (i - dp[i - 1] >= 1 && s.charAt(i - dp[i - 1] - 1) == '(') {
                    // 中间一定存在括号对，看左侧是否存在括号对
                    dp[i] = dp[i - 1] + (i - dp[i - 1] - 2 >= 0 ? dp[i - dp[i - 1] - 2] : 0) + 2;
                }
                max = Math.max(max, dp[i]);
            }
        }
        return max;
    }

